mapper.select:
SELECT RNO, DID, DTITLE, DSFILE, DCODE, RID, TO_CHAR(RDATE, 'YYYY-MM-DD') RDATE, TO_CHAR(ROKDATE, 'YYYY-MM-DD') ROKDATE, RTOTAL, RCHECK FROM (SELECT ROWNUM RNO, DID, DTITLE, DSFILE, DCODE, RID, RDATE, ROKDATE, RTOTAL, RCHECK FROM (SELECT DT.DID, DT.DTITLE, DT.DSFILE, DT.DCODE, DR.RID, DR.RDATE, DR.ROKDATE, DR.RTOTAL, DR.RCHECK FROM DMU_TICKET DT, DMU_RESERVATION DR WHERE DT.DID = DR.DID AND DR.MID = #{mid} ORDER BY ROKDATE DESC)) WHERE RNO BETWEEN #{start} AND #{end}
AND DCODE = #{dcode}
AND ROKDATE BETWEEN #{startdate} AND TO_DATE(#{enddate}, 'YYYY-MM-DD')+1
AND ROKDATE BETWEEN (SYSDATE-7) AND SYSDATE
AND ROKDATE BETWEEN (SYSDATE-7) AND SYSDATE

SELECT COUNT(*) FROM DMU_TICKET DT, DMU_RESERVATION DR WHERE DT.DID = DR.DID AND DR.MID = #{mid}
AND DT.DCODE = #{dcode}
AND ROKDATE BETWEEN #{startdate} AND TO_DATE(#{enddate}, 'YYYY-MM-DD')+1
AND ROKDATE BETWEEN (SYSDATE-7) AND SYSDATE
AND ROKDATE BETWEEN (SYSDATE-7) AND SYSDATE

SELECT DP.PDATE, DP.PALLPRICE, DTI.TID, DTI.TCHECK
FROM DMU_TICKETINFO DTI, DMU_PAYINFO DP, DMU_RESERVATION DR
WHERE DTI.RID = DP.RID AND DTI.RID = DR.RID AND  DTI.RID = #{rid}
