mapper.select:
select did, dtitle, dplace,dsfile, dcode,dstart from
(select did, dtitle, dplace,dsfile, dcode,dstart from dmu_ticket where dend < sysdate )
where dplace= #{dplace}  order by dstart;

select * from
(select did,dplace,dtitle,dstart,dend,dfile,dsfile,dcode,dtime,dprice,dtarget,dnum ,dpersonnel,dtitle2,dentertime from dmu_ticket where dcode =#{dcode}
and dtarget= #{dtarget}) where dend > sysdate   order by dstart asc

select * from
(select did,dplace,dtitle,dstart,dend,dfile,dsfile,dcode,dtime,dprice,dtarget,dnum ,dpersonnel,dtitle2,dentertime from dmu_ticket where dcode =#{dcode}
and dtarget= #{dtarget}) where dend > sysdate   order by dstart desc
